package org.pxcbpmisframework.core.common.datasource;import java.util.Map;import org.apache.logging.log4j.LogManager;import org.apache.logging.log4j.Logger;import org.springframework.jdbc.datasource.lookup.AbstractRoutingDataSource;import org.springframework.jdbc.datasource.lookup.DataSourceLookup;public class DynamicDataSource extends AbstractRoutingDataSource {	private static final Logger logger = LogManager.getLogger(DynamicDataSource.class);	protected Object determineCurrentLookupKey() {				String dataSourceName = DataSourceContextHolder.getDataSourceType();		logger.info("***************** 切换数据源中 [dataSourceName]   *****************");		if (dataSourceName == null) {			dataSourceName = DataSourceType.values()[0].toString();		}		logger.info("***************** 正在使用的数据源：[" + dataSourceName + "]");		return dataSourceName;	}	public void setDataSourceLookup(DataSourceLookup dataSourceLookup) {		super.setDataSourceLookup(dataSourceLookup);	}	public void setDefaultTargetDataSource(Object defaultTargetDataSource) {		super.setDefaultTargetDataSource(defaultTargetDataSource);	}	public void setTargetDataSources(Map<Object, Object> targetDataSources) {		super.setTargetDataSources(targetDataSources);	}}